mapper.select:
SELECT * FROM user

ORDER BY ${orderby} ${direct}
LIMIT ${startIdx},${pageRow}

mapper.update:
UPDATE user SET
	pwd=md5(#{pwd}), email=#{email}
	, tel=#{tel} , zipcode=#{zipcode}
	, addr1=#{addr1} , addr2=#{addr2}
WHERE no=#{no}

UPDATE user SET
	pwd = md5(#{pwd})
WHERE id=#{id} AND email=#{email}
